import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { register, login, logout } from './authAPI'

/**
 * 'window.localStorage' is a browser feature that stores data as a <string>
 * - localStorage.setItem()
 * - localStorage.getItem()
 * - localStorage.removeItem()
 * - localStorage.clear()
 */
// const user = JSON.parse(localStorage.getItem('user'))
/***************************************************/

const initialState = {
    // user: user ? user : null,
    user: null,
    message: '',
    // status: 'idle' || 'loading' || 'success' || 'error'
    isLoading: false,
    isSuccess: false,
    isError: false
}

export const registerUser = createAsyncThunk(
    'auth/registerUser',
    async (userData, thunkAPI) => {
        try {
            const response = await register(userData)
            // console.log(response)
            return response.data.message // return <Payload>
        } catch (error) {
            // console.log(error.response.data.message)
            // console.log(error.message)
            // console.log(error.toString())
            const message = error.response.data.message || error.message || error.toString()
            return thunkAPI.rejectWithValue(message)
        }
    }
)

export const loginUser = createAsyncThunk(
    'auth/loginUser',
    async (userData, thunkAPI) => {
        try {
            const res = await login(userData)
            // console.log(res)
            /****************************************************/
            // localStorage.setItem('user', JSON.stringify(res.data))
            /****************************************************/
            return res.data // return <Payload>
        } catch (error) {
            const message = error.response.data.message || error.message || error.toString()
            return thunkAPI.rejectWithValue(message)
        }
    }
)

export const logoutUser = createAsyncThunk(
    'auth/logoutUser',
    async (_, thunkAPI) => {
        try {
            const res = await logout()
            // console.log(res)
            return res.data.message // return <Payload>
        } catch (error) {
            const message = error.response.data.message || error.message || error.toString()
            return thunkAPI.rejectWithValue(message)
        }
    }
)

export const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        reset: state => {
            state.user = null
            state.message = ''
            // status: 'idle' || 'loading' || 'success' || 'error'
            state.isError = false
            state.isLoading = false
            state.isSuccess = false
        }
    },
    extraReducers: builder => {
        builder.addCase(registerUser.pending, state => {
                // state.status = 'loading'
                state.isLoading = true
            }).addCase(registerUser.fulfilled, (state, action) => {
                // state.status = 'success'
                state.isLoading = false
                state.isSuccess = true
                state.message = action.payload
            }).addCase(registerUser.rejected, (state, action) => {
                // state.status = 'error'
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
        builder.addCase(loginUser.pending, state => {
                // state.status = 'loading'
                state.isLoading = true
            }).addCase(loginUser.fulfilled, (state, action) => {
                // state.status = 'success'
                state.isLoading = false
                state.isSuccess = true
                state.user = action.payload
            }).addCase(loginUser.rejected, (state, action) => {
                // state.status = 'error'
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
        builder.addCase(logoutUser.pending, state => {
                // state.status = 'loading'
                state.isLoading = true
            }).addCase(logoutUser.fulfilled, (state, action) => {
                // state.status = 'success'
                state.isLoading = false
                state.isSuccess = true
                state.message = action.payload
            }).addCase(logoutUser.rejected, (state, action) => {
                // state.status = 'error'
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
    }
})

export const { reset } = authSlice.actions

export const selectAuth = state => state.auth

export default authSlice.reducer
