        //         state.message = action.payload            
        //         // state.status = 'error'
        //         state.isLoading = false
        //         state.isError = true
        //     })
        ///////////////////////////////////////////////////////////
        /* Solution2 */
        // builder.addCase(logoutUser.fulfilled, state => { 
        //         state.user = null // !!!
        //     })
