import { useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { selectAuth } from '../features/auth/authSlice'
import { fetchGoals, reset, removeItems, selectGoals } from '../features/goals/goalsSlice'
//////////////////////////////////////////////////////
import Spinner from './Spinner'


const Goals = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const { user } = useSelector(selectAuth)
    const { items, isLoading, isSuccess, isError } = useSelector(selectGoals)

    useEffect(() => {

        // else {

        // }
        if (isSuccess || isError) {
            dispatch(reset())
        }
        if (!user) {
            navigate('/login')
        }
        dispatch(fetchGoals()) 

        return () => { 
                dispatch(removeItems()) 
            }
    }, [ user, navigate, dispatch ])

    let goalItems
    if (items.length === 0) {
        goalItems = <h4>- No Goals -</h4>
    } else {
        goalItems = items.map((goal, idx) => 
            <li key={ idx } post={ goal }>{ goal.text }</li>
        )
    }

    if (isLoading) return <Spinner />

    return (
        <div>
            <h3>Latest Goals</h3>
            <ul>
                { goalItems }
            </ul>
        </div>
    )
}

export default Goals
