import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { fetchAll, create, 
    // update, 
    // destroy 
} from './goalsAPI'


const goals = JSON.parse(localStorage.getItem('goals'))

const initialState = {
    items: goals ? goals : [],
    item: null,
    message: '',
    isLoading: false,
    isSuccess: false,
    isError: false
}

export const fetchAllGoals = createAsyncThunk(
    'goals/fetchAllGoals',
    async (token, thunkAPI) => {
        try {
            const res = await fetchAll(token)
            localStorage.setItem('goals', JSON.stringify(res.data))
            return res.data
        } catch (error) {
            const message = error.response.data.message || error.message || error.toString()
            return thunkAPI.rejectWithValue(message)
        }
    }
)

export const createGoal = createAsyncThunk(
    'goals/createGoal',
    async ({ goalData, token }, thunkAPI) => {
        try {
            const res = await create(goalData, token)
            localStorage.setItem('goal', JSON.stringify(res.data))
            return res.data
        } catch (error) {
            const message = error.response.data.message || error.message || error.toString()
            return thunkAPI.rejectWithValue(message)
        }
    }
)

