export const goalsSlice = createSlice({
    name: 'goals',
    initialState,
    reducers: {
        reset: state => {
            state.message = ''
            state.isLoading = false
            state.isSuccess = false 
            state.isError = false
        }
    },
    extraReducers: build => {
        build.addCase(fetchAllGoals.pending, state => {
                state.isLoading = true
            }).addCase(fetchAllGoals.fulfilled, (state, action) => {
                state.isLoading = false
                state.isSuccess = true
                state.items = action.payload
            }).addCase(fetchAllGoals.rejected, (state, action) => {
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
        build.addCase(createGoal.pending, state => {
                state.isLoading = true
            }).addCase(createGoal.fulfilled, (state, action) => {
                state.isLoading = false
                state.isSuccess = true
                state.item = action.payload
            }).addCase(createGoal.rejected, (state, action) => {
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
    }
})

export const { reset } = goalsSlice.actions

export const selectGoalsState = state => state.goals

export default goalsSlice.reducer
