import { useState } from 'react'
import { useDispatch } from 'react-redux'
import { destroyGoal } from '../features/goals/goalsSlice'


const GoalItem = ({ goal }) => {
    const [ currentGoal, setCurrentGoal ] = useState({
        text: goal.text ? goal.text : '',
        isUpdated: false
    })

    const handleChange = e => {
        setCurrentGoal((prevState) => ({
            ...prevState,
            text: e.target.value
        }))
    }

    const dispatch = useDispatch()

    const handleDelete = () => {
        dispatch(destroyGoal(goal._id))
    }

    const handleUpdate = () => {
        setCurrentGoal((prevState) => ({
            ...prevState,
            isUpdated: !prevState.isUpdated
        }))
    }

    return (
        <div className="goal">
            {
                !currentGoal.isUpdated ?
                <h3>{ goal.text }</h3> :
                <input type="text" onChange={ handleChange } value={ currentGoal.text } /> 
            }

            <div style={ { display: "inline-block"} }>
                {/* { new Date(goal.createdAt).toLocaleString('ko-KR') } */}
                { new Date(goal.createdAt).toLocaleString('en-US') }
            </div>
            <div>
                <button className='rewrite' onClick={ handleUpdate }>
                    Rewrite
                </button>
                <button className='close' onClick={ handleDelete }>
                    X
                </button>
            </div>
        </div>
    )
}

export default GoalItem
