import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchAllGoals, selectGoalsState } from '../features/goals/goalsSlice'

const Goals = () => {
    const dispatch = useDispatch()
    const { items } = useSelector(selectGoalsState)

    useEffect(() => {
        dispatch(fetchAllGoals())
    }, [ dispatch ])

    let goalItems
    if (!items || items.length === 0) {
        goalItems = <h3>No Goals</h3>
    } else {
        goalItems = items.map((goal, idx) => 
            (<li key={ idx } post={ goal }>{ goal.text }</li>)
        )
    }

    return (
        <div>
            Goals
            <ul>
                { goalItems }
            </ul>
        </div>
    )
}

export default Goals
