import { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { selectAuth } from '../features/auth/authSlice'
import { createGoal } from '../features/goals/goalsSlice'
/////////////////////////////////////////////////////////
import { toast } from 'react-toastify'


const Setter = () => {
    const [ goal, setGoal ] = useState('')
    const dispatch = useDispatch()
    const { user, message } = useSelector(selectAuth)

    const handleChange = e => {
        setGoal(e.target.value)
    }

    const handleSubmit = e => {
        e.preventDefault()
        if (goal === '') {
            return toast.error(message, { autoClose: 750 })
        }
        const newGoal = { text: goal }
        dispatch(createGoal({ goalData: newGoal, token: user.token }))
        setGoal('')
    }

    return (
        <div>
            <form onSubmit={ handleSubmit }>
                <input type="text" 
                    name='goal'
                    onChange={ handleChange }
                    value={ goal } 
                    placeholder='Enter your goal' />
            </form>
        </div>

    )
}

export default Setter
