import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { selectAuth } from '../features/auth/authSlice'
import { fetchGoals, selectGoals } from '../features/goals/goalsSlice'

const Goals = () => {
    const dispatch = useDispatch()
    const { user } = useSelector(selectAuth)
    const { items } = useSelector(selectGoals)
    console.log(items)

    // useEffect(() => {
    //     dispatch(fetchGoals(user.token))
    // }, [ dispatch, user.token ])

    let goalItems
    if (!items || items.length === 0) {
        goalItems = <h3>No Goals</h3>
    } else {
        goalItems = items.map((goal, idx) => 
            <li key={ idx } post={ goal }>{ goal.text }</li>
        )
    }

    return (
        <div>
            Goals
            <ul>
                { goalItems }
            </ul>
        </div>
    )
}

export default Goals
