export const goalsSlice = createSlice({
    name: 'goals',
    initialState,
    reducers: {
        reset: state => {
            state.message = ''
            state.isLoading = false
            state.isSuccess = false
            state.isError = false
        },
        removedItems: state => {
            state.items = []
        }
    },
    extraReducers: build => {
        build.addCase(fetchGoals.pending, state => {
                state.isLoading = true
            }).addCase(fetchGoals.fulfilled, (state, action) => {
                state.isLoading = false
                state.isSuccess = true
                state.items = action.payload
            }).addCase(fetchGoals.rejected, (state, action) => {
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
        build.addCase(createGoal.pending, state => {
                state.isLoading = true
            }).addCase(createGoal.fulfilled, (state, action) => {
                state.isLoading = false
                state.isSuccess = true
                // state.items.unshift(action.payload)
                ////////////////////////////////////////////////
                state.items = [ action.payload, ...state.items ]
            }).addCase(createGoal.rejected, (state, action) => {
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
        build.addCase(destroyGoal.pending, state => {
                state.isLoading = true
            }).addCase(destroyGoal.fulfilled, (state, action) => {
                console.log(action.payload.id)
                state.isLoading = false
                state.isSuccess = true
                state.items = state.items.filter(item => item._id !== action.payload.id)
            }).addCase(destroyGoal.rejected, (state, action) => {
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
    }
})

export const { reset, removedItems } = goalsSlice.actions

export const selectGoals = state => state.goals

export default goalsSlice.reducer
