import { useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { logoutUser, reset, selectAuth } from '../features/auth/authSlice'
/////////////////////////////////////////////////////////////////////////
import { FaSignInAlt, FaSignOutAlt, FaUser } from 'react-icons/fa'
import { toast } from 'react-toastify'
import Spinner from '../components/Spinner'


const Header = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const currentUser = JSON.parse(localStorage.getItem('user'))
    // console.log(currentUser)
    const { message, isLoading, isSuccess, isError } = useSelector(selectAuth)

    const handleClick = async () => {
        await dispatch(logoutUser())
        if (isSuccess) {
            if (message) toast.success(message, { autoClose: 1000})
            else toast.success('Logout Success!', { autoClose: 1000})
            dispatch(reset())
            localStorage.clear() // !!!
            navigate('/')
        }
        if (isError) {
            toast.error(message, { autoClose: 1000})
            dispatch(reset())
            navigate('/dashboard')
        }
    }

    if (isLoading) return <Spinner />

    return (
        <header className='header'>
            <div className='logo'>
                <Link to='/'>
                    Goal Setter
                </Link>
            </div>
            <ul>
                {
                    !currentUser ?
                        <>
                            <li>
                                <Link to='/login'>
                                    <FaSignInAlt />Login
                                </Link>
                            </li>
                            <li>
                                <Link to='/register'>
                                    <FaUser />Register
                                </Link>
                            </li>
                        </>
                    :
                        <li>
                            <button className='btn' onClick={ handleClick }>
                                <FaSignOutAlt />Logout
                            </button>
                        </li>
                }
            </ul>
        </header>
    )
}

export default Header
