import { useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { logoutUser, reset, selectAuth } from '../features/auth/authSlice'
/////////////////////////////////////////////////////////////////////////
import { FaSignInAlt, FaSignOutAlt, FaUser } from 'react-icons/fa'
import { toast } from 'react-toastify'


const Header = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const { user, message, isLoading, isSuccess, isError } = useSelector(selectAuth)

    useEffect(() => {
        if (isLoading) toast.loading('Loading...')
        if (isSuccess) {
            if (message) toast.success(message)
            else toast.success('Success!')
        }
        if (isError) toast.error(message)
    }, [ dispatch, navigate, user, message, isLoading, isSuccess, isError ])

    const handleClick = () => {
        dispatch(logoutUser())
        dispatch(reset())
        // localStorage.removeItem('user')
        navigate('/')
    }

    return (
        <header className='header'>
            <div className='logo'>
                <Link to='/'>
                    Goal Setter
                </Link>
            </div>
            <ul>
                <li>
                    <Link to='/login'>
                        <FaSignInAlt />Login
                    </Link>
                </li>
                <li>
                    <Link to='/register'>
                        <FaUser />Register
                    </Link>
                </li>
                <li onClick={ handleClick }>
                    <button className='btn'>
                        <FaSignOutAlt />Logout
                    </button>
                </li>
            </ul>
        </header>
    )
}

export default Header
