import { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { selectAuth } from '../features/auth/authSlice'
import { createGoal } from '../features/goals/goalsSlice'
/////////////////////////////////////////////////////////
import { toast } from 'react-toastify'


const GoalForm = () => {
    const [ goal, setGoal ] = useState('')
    const dispatch = useDispatch()
    const { user, message } = useSelector(selectAuth)

    const handleChange = e => {
        setGoal(e.target.value)
    }

    const handleSubmit = e => {
        e.preventDefault()
        if (goal === '') {
            return toast.error(message, { autoClose: 750 })
        }
        const newGoal = { text: goal }
        dispatch(createGoal({ goalData: newGoal, token: user.token }))
        setGoal('')
    }

    return (
        <section className='form'>
            <form onSubmit={ handleSubmit }>
                <div className="form-group">
                    <label htmlFor="goal">Goal : </label>
                    <input type="text"
                        name='goal'
                        onChange={ handleChange }
                        value={ goal } 
                        placeholder='Enter your goal' />
                </div>
                <button type="submit" className='btn btn-block'>
                    Submit
                </button>
            </form>
        </section>

    )
}

export default GoalForm
